export function extractEnvironment(options: Cypress.PluginConfigOptions): Record<string, any> {
  const versions = {} as Record<string, string>
  try {
    const {name, version} = require('cypress/package.json')
    versions[name] = version
  } catch {
    // NOTE: ignore error
  }
  let sdk = undefined as {lang: string; name: string; currentVersion: string} | undefined
  try {
    const {name, version} = require('../../package.json')
    sdk = {lang: 'js', name, currentVersion: version}
  } catch {
    // NOTE: ignore error
  }
  return {versions, sdk, testingType: options?.testingType ?? 'e2e'}
}
